import React, {Component } from 'react';

class Summary extends Component{

	render(){
		const {summary, currentDate} = this.props;
		return (
	  		<div className="row1">

	  			<h1>Last updated on : <strong >{new Date(currentDate).toDateString()}</strong></h1>

	  			<div className="NewConfirmed">
	  				<h3>Active</h3>
	  				<h1>{summary.active}</h1>
	  			</div>

	  			<div className="TotalConfirmed">
	  				<h3>Confirmed</h3>
	  				<h1>{summary.confirmed}</h1>
	  			</div>

	  			<div className="NewDeaths">
	  				<h3>Deaths</h3>
	  				<h1>{summary.deaths}</h1>
	  			</div>

	  			<div className="TotalDeaths">
	  				<h3>TotalDeaths</h3>
	  				<h1>{summary.TotalDeaths}</h1>
	  			</div>

	    	</div>
		);
	}
}

export default Summary;
