import React, {Component } from 'react';
import axios from 'axios';

import Summary2 from './Summary2';
import Navbar from './Navbar';
import States from './States';

class Home extends Component{

	state = {
		cases_time_series: [],
		states:[],
		currentDate : null,
		loading : true
	}
	async componentDidMount() {
		const res1 = await axios.get("https://api.covid19india.org/data.json");
		console.log(res1);
		const res2 = await axios.get("https://api.covid19india.org/state_district_wise.json");
		console.log(res2);
		this.setState({cases_time_series : res1.data.cases_time_series});
		this.setState({states : res1.data.statewise});
		this.setState({currentDate : res1.data.statewise[0].lastupdatedtime});
		this.setState({loading : false});
	}

  render(){
  	if(this.state.loading){
  		return <h1 >Loading...</h1>
  	}
	return (
	  	<div>
	  		<Navbar/>
	  		<Summary2 summary = {this.state.states[0]} currentDate = {this.state.currentDate}/>	
	    	<table>
	    		<thead>
	    			<tr>
			    		<th> State(/UT) </th>
			    		<th> Active</th>
			    		<th> Total Confirmed</th>
			    		<th> Total Deaths</th>
			    		<th> Total Recovered</th>
			    	</tr>
				</thead>
				<tbody>
					{this.state.states.map( state => (
						<States states = {state} key = {country.Country}/>
					))}
				</tbody>
    	</table>
	    </div>
	);
}
}

export default Home;
