import React, {Component } from 'react';
import axios from 'axios';
import Summary2 from './Summary';
import Navbar from './Navbar';
class Details extends Component{

	state = {
		cases_time_series: [],
		statewise:[],
		currentDate : null,
		loading : true
	}
	async componentDidMount() {
		const res1 = await axios.get("https://api.covid19india.org/data.json");
		console.log(res1);
		const res2 = await axios.get("https://api.covid19india.org/state_district_wise.json");
		console.log(res2);
		this.setState({cases_time_series : res1.data.cases_time_series});
		this.setState({statewise : res1.data.statewise});
		this.setState({currentDate : res1.data.statewise[0].lastupdatedtime});
		this.setState({loading : false});
	}

  render(){
  	if(this.state.loading){
  		return <h1 >Loading...</h1>
  	}
	return (
	  	<div>
	  		<Navbar/>
	  		<Summary2 summary = {this.state.statewise[0]} currentDate = {this.state.currentDate}/>	
	    </div>
	);
}
}

export default Details;
